y.fetchRequest()        request.fetchLimit = 1        if let entity = try? context.fetch(request).first {            entity.update(from: profile)        } else {            let newEntity = UserProfileEntity(context: context)            newEntity.update(from: profile)        }        try? context.save()    }    func updatePassword(        email: String,        oldPassword: String,        newPassword: String,        completion: @escaping (Bool) -> Void    ) {        let request: NSFetchRequest<UserProfileEntity> =            UserProfileEntity.fetchRequest()        request.predicate = NSPredicate(            format: "email == %@ AND password == %@",            email,            oldPassword        )        request.fetchLimit = 1        if let entity = try? context.fetch(request).first {            entity.password = newPassword            try? context.save()            completion(true)        } else {            completion(false)        }    }}