import SwiftUIimport WebKitstruct YouTubePlayerView: UIViewRepresentable {    let videoID: String    func makeUIView(context: Context) -> WKWebView {        let webView = WKWebView()        webView.scrollView.isScrollEnabled = false        webView.configuration.allowsInlineMediaPlayback = true        webView.configuration.mediaTypesRequiringUserActionForPlayback = []        let embedHTML = """        <!DOCTYPE html>        <html>        <body style="margin:0;">        <iframe width="100%" height="100%" src="https://www.youtube.com/embed/\(videoID)?playsinline=1"        frameborder="0" allow="autoplay; encrypted-media" allowfullscreen></iframe>        </body>        </html>        """        webView.loadHTMLString(embedHTML, baseURL: nil)        return webView    }    func updateUIView(_ uiView: WKWebView, context: Context) {}}